import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Avatar } from "@material-ui/core";
import db from "./firebase";
import { Link } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  SideBarChat: {
    display: "flex",
    padding: "20px",
    cursor: "pointer",
    borderBottom: "1px solid #f6f6f6",
  },
}));

function SideBarChat({ addNewChat,name,id }) {
  const [seed, setSeed] = useState(0);
  const [messages, setMessages] = useState(0);

  useEffect(() => {
    setSeed(Math.floor(Math.random() * 5000));
  }, []);
  useEffect(() => {
    setSeed(Math.floor(Math.random() * 5000));
    let roomNameCleanup = "";
    let messagesCleanup = "";
    if (id) {
      messagesCleanup = db
        .collection("rooms")
        .doc(roomId)
        .collection("messages")
        .orderBy("timestamp", "desc")
        .onSnapshot((snapshoot) =>
          setMessages(snapshoot.docs.map((doc) => doc.data()))
        );
    }

    return () => {
      roomNameCleanup();
      messagesCleanup();
    };
  }, [id]);

  const classes = useStyles();
  let createNewChat = () => {
      const roomName=prompt("pleae enter chat room")
      if(roomName){
          //DB goes here
          db.collection("rooms").add({name:roomName})
          console.log(roomName)
      }
  };
  return !addNewChat ? (
      <Link to={`/rooms/${id}`}>
    <div className={classes.SideBarChat}>
      <Avatar src={`https://avatars.dicebear.com/api/human/${seed}.svg`} />
      <div className={classes.SideBarChatInfo}>
        <h2>{name}</h2>
        <p>last message...</p>
      </div>
    </div>
    </Link>
  ) : (
    <div onClick={createNewChat} className={classes.SideBarChat}>
      <h2>Add new chat</h2>
    </div>
  );
}

export default SideBarChat;
