    placeItems: "center",
  },
  app_body: {
    display: "flex",
    backgroundColor: "#ededed",
    height: "95%",
    maxHeight: "750px",
    width: "70%",
    boxShadow: "-1px 4px 20px -6px rgba(0,0,0,0.7)",
  },
  SideBar: {
    width: "100%",
    height: "100%",
    display: "flex",
  },
}));

function App() {

  const classes = useStyles();

  return (
    <div className={classes.app}>
      <div className={classes.app_body}>
        <div className={classes.SideBar}>
