import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Avatar, IconButton } from "@material-ui/core";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import SearchOutlined from "@material-ui/icons/SearchOutlined";
import AttachFile from "@material-ui/icons/AttachFile";
import InsertEmoticonIcon from "@material-ui/icons/InsertEmoticon";
import SendIcon from "@material-ui/icons/Send";
const useStyles = makeStyles((theme) => ({
  chat: {
    flex: "0.65",
    display: "flex",
    flexDirection: "column",
  },
  chatHeader: {
    padding: "20px",
    display: "flex",
    alignItems: "center",
    borderBottom: "1px solid lightgray",
  },
  chatHeaderInfo: {
    flex: "1",
  },
  chatHeaderRight: {
    marginLeft: "auto",
  },
  chatBody: {
    flex: "1",
    backgroundImage:
      "url('https://user-images.githubusercontent.com/15075759/28719144-86dc0f70-73b1-11e7-911d-60d70fcded21.png')",
    backgroundRepeat: "repeat",
    backgroundPosition: "center",
    padding: "30px",
    overflow: "auto",
  },
  chatMessage: {
    position: "relative",
    fontSize: "16px",
    padding: "10px",
    backgroundColor: "#ffffff",
    borderRadius: "10px",
    width: "fit-content",
    display: "flex",
  },
  reciver: {
    marginLeft: "auto",
    backgroundColor: "#dcf8c6",
  },

  chatName: {
    position: "absolute",
    top: "-15px",
    fontSize: "10px",
  },
  chatTimeStamp: {
    fontSize: "xx-small",
    marginLeft: "6px",
    alignSelf: "flex-end",
  },

  chatFooter: {},
}));
function Chat() {
  const classes = useStyles();
  const [seed, setSeed] = useState(0);

  useEffect(() => {
    setSeed(Math.floor(Math.random() * 5000));
  }, []);
  return (
    <div className={classes.chat}>
      <div className={classes.chatHeader}>
        <Avatar src={`https://avatars.dicebear.com/api/human/${seed}.svg`} />
        <div className={classes.chatHeaderInfo}>
          <h3>Room Name</h3>
          <p>last seen ...</p>
        </div>
        <div className={classes.chatHeaderRight}>
          <IconButton>
            <SearchOutlined />
          </IconButton>
          <IconButton>
            <AttachFile />
          </IconButton>
          <IconButton>
            <MoreVertIcon />
          </IconButton>
        </div>
      </div>
      <div className={classes.chatBody}>
        <p className={classes.chatMessage}>
          <span className={classes.chatName}> room name</span>
          hey everyone
          <span className={classes.chatTimeStamp}>3:15pm</span>
        </p>
        <p className={`${classes.chatMessage} ${classes.reciver}`}>
          <span className={classes.chatName}> room name</span>
          hey everyone
          <span className={classes.chatTimeStamp}>3:15pm</span>
        </p>
      </div>

      <div className={classes.chatFooter}>
        <InsertEmoticonIcon />
        <form>
          <input type="text" />
          <IconButton>
            <Send />
          </IconButton>
        </form>
      </div>
    </div>
  );
}

export default Chat;
